mapper.select:
SELCET uid, username, password, nickname, created_at
  FROM tbl_user
 ORDER BY uid ${SORT}
 LIMIT #{offset}, #{size}

SELECT uid, username, password, nickname, created_at
  FROM tbl_user
 WHERE uid =#{uid}

mapper.update:
UPDATE tbl_user
   SET password = #{password},
       nickname = #{nickname}
 WHERE uid = #{uid}
